import React, { useState } from 'react';
import axios from 'axios';
import './REPL.css'

const REPL = () => {
    const [input, setInput] = useState('');
    const [output, setOutput] = useState([]);
    const [compiledOutput, setCompiledOutput] = useState([]);
    const [isSkip, setIsSkip] = useState(false);

    const handleInputChange = (e) => {
        setInput(e.target.value);
    };

    const handleInputSubmit = async (e) => {
        e.preventDefault();
        setOutput([...output, `${input}`]);
        const payload = {}
        /* contacting API for code compilation */
        if (isSkip)
        try {
            const resp = await axios.post('http://127.0.0.1:5000/compile', {code: output.join('\n') + '\n' + input});
            /* factor in returning errors in the output instead of just output */
            const compiledResult = resp.data.output;
            console.log(compiledResult);
            if (compiledResult) {
                setCompiledOutput([compiledResult]);
                setIsSkip(true);
            } else {
                setCompiledOutput([]);
                setIsSkip(false);
            }
        } catch (error) {
            console.error('Error: ', error);
        }
        setInput('');
    };

    return (
        <div>
            <div>
                {output.map((line, index) => (
                    <div key={index}><p className='cursor'>&gt;&gt;&gt;</p>{line}</div>
                ))}
            </div>
            <div id='repl'>
                {compiledOutput}
                {console.log(compiledOutput[0])}
                {typeof compiledOutput[0] === 'string' && (
                    <br/>
                )}
                <p className='cursor'>&gt;&gt;&gt;</p>
                <form onSubmit={handleInputSubmit}>
                    <input
                        type="text"
                        value={input}
                        onChange={handleInputChange}
                        placeholder="Enter Python code here..."
                    />
                </form>
            </div>
        </div>
    );
};

export default REPL;
